  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 1px 2px 0px hsl(210 10% 15% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(210 10% 15% / 0.08);
  --shadow-sm: 0px 2px 4px -1px hsl(210 10% 15% / 0.06), 0px 1px 2px -1px hsl(210 10% 15% / 0.04);
  --shadow: 0px 4px 6px -1px hsl(210 10% 15% / 0.08), 0px 2px 4px -2px hsl(210 10% 15% / 0.05);
  --shadow-md: 0px 6px 8px -2px hsl(210 10% 15% / 0.1), 0px 4px 6px -2px hsl(210 10% 15% / 0.06);
  --shadow-lg: 0px 10px 15px -3px hsl(210 10% 15% / 0.12), 0px 4px 6px -4px hsl(210 10% 15% / 0.08);
  --shadow-xl: 0px 20px 25px -5px hsl(210 10% 15% / 0.14), 0px 8px 10px -6px hsl(210 10% 15% / 0.1);
