server/migrations/migrate-chatroom-ids.j.content:
import mongoose from 'mongoose';
import Chat from '../models/Chat.js';
import dotenv from 'dotenv';

dotenv.config();

async function migrateChatRoomIds() {
  try {
    const MONGODB_URI = process.env.MONGODB_URI || 'mongodb://localhost:27017/swapchat';
    await mongoose.connect(MONGODB_URI);
    console.log('âœ“ MongoDB connected');

    const allChats = await Chat.find({}).lean({ getters: true });
    console.log(`Found ${allChats.length} chat messages to migrate`);

    let updated = 0;
    let skipped = 0;

    for (const chat of allChats) {
      try {
        const senderId = chat.senderId;
        const receiverId = chat.receiverId;

        if (!senderId || !receiverId) {
          console.log(`Skipping chat ${chat._id} - missing sender or receiver`);
          skipped++;
          continue;
        }

        const plainChatRoomId = [senderId, receiverId].sort().join('_');

        if (chat.chatRoomId === plainChatRoomId) {
          skipped++;
          continue;
        }

        await Chat.updateOne(
          { _id: chat._id },
          { $set: { chatRoomId: plainChatRoomId } }
        );

        updated++;

        if (updated % 100 === 0) {
          console.log(`Progress: ${updated} messages updated, ${skipped} skipped`);
        }
      } catch (error) {
        console.error(`Error updating chat ${chat._id}:`, error.message);
      }
    }

    console.log('\nâœ“ Migration completed!');
    console.log(`  Updated: ${updated} messages`);
    console.log(`  Skipped: ${skipped} messages (already correct or invalid)`);
    console.log(`  Total: ${allChats.length} messages`);

    await mongoose.disconnect();
    console.log('âœ“ MongoDB disconnected');
    process.exit(0);
  } catch (error) {
    console.error('Migration failed:', error);
    process.exit(1);
  }
}

migrateChatRoomIds();

client/src/contexts/ThemeContext.tsx.content:
import { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { apiRequest } from '@/lib/queryClient';

type Theme = 'light' | 'dark';

interface ThemeContextType {
  theme: Theme;
  toggleTheme: () => void;
  setTheme: (theme: Theme) => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

export function ThemeProvider({ children }: { children: ReactNode }) {
  const [theme, setThemeState] = useState<Theme>('dark');
  const [isInitialized, setIsInitialized] = useState(false);

  const loadThemeFromLocalStorage = () => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const user = JSON.parse(storedUser);
        const userTheme = user.themePreference || 'dark';
        setThemeState(userTheme);
        document.documentElement.classList.toggle('dark', userTheme === 'dark');
        console.log(`ðŸŽ¨ Loaded theme preference: ${userTheme} for user ${user.username}`);
      } catch (error) {
        console.error('Error parsing user data:', error);
      }
    }
  };

  useEffect(() => {
    loadThemeFromLocalStorage();
    setIsInitialized(true);
  }, []);

  useEffect(() => {
    const handleStorageChange = (e: StorageEvent) => {
      if (e.key === 'user' || e.key === null) {
        console.log('ðŸ”„ User data changed in localStorage, reloading theme...');
        loadThemeFromLocalStorage();
      }
    };

    const handleUserChange = () => {
      loadThemeFromLocalStorage();
    };

    window.addEventListener('storage', handleStorageChange);
    window.addEventListener('user-logged-in', handleUserChange);

    const interval = setInterval(() => {
      const storedUser = localStorage.getItem('user');
      if (storedUser) {
        try {
          const user = JSON.parse(storedUser);
          const userTheme = user.themePreference || 'dark';
          if (userTheme !== theme) {
            console.log('ðŸ”„ Theme preference mismatch detected, syncing...');
            loadThemeFromLocalStorage();
          }
        } catch (error) {
          // Ignore parsing errors
        }
      }
    }, 500);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('user-logged-in', handleUserChange);
      clearInterval(interval);
    };
  }, [theme]);

  useEffect(() => {
    if (!isInitialized) return;

    const root = document.documentElement;
    root.classList.remove('light', 'dark');
    root.classList.add(theme);

    root.style.transition = 'background-color 0.3s ease, color 0.3s ease';
  }, [theme, isInitialized]);

  const setTheme = async (newTheme: Theme) => {
    setThemeState(newTheme);
    document.documentElement.classList.toggle('dark', newTheme === 'dark');

    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const user = JSON.parse(storedUser);
        user.themePreference = newTheme;
        localStorage.setItem('user', JSON.stringify(user));

        await apiRequest('PUT', '/api/users/theme', { theme: newTheme });
        console.log(`âœ… Theme updated to ${newTheme} for user and saved to backend`);
      } catch (error) {
        console.error('Failed to save theme preference:', error);
      }
    }
  };

  const toggleTheme = () => {
    const newTheme = theme === 'dark' ? 'light' : 'dark';
    setTheme(newTheme);
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme, setTheme }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (context === undefined) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
}

