import Chat from '../models/Chat.js';
import GhostMessage from '../models/GhostMessage.js';
import fs from 'fs';
import path from 'path';

const DELETION_INTERVAL = 10000;
const BATCH_SIZE = 100;

let deletionWorkerRunning = false;
let deletionInterval = null;
let ioInstance = null;
let userSocketsRef = null;

async function deleteMediaFile(mediaUrl) {
  if (!mediaUrl) return;

  try {
    if (mediaUrl.startsWith('/uploads/')) {
      const filePath = path.join(process.cwd(), mediaUrl);
      if (fs.existsSync(filePath)) {
        fs.unlinkSync(filePath);
        console.log(`ðŸ—‘ï¸ Deleted media file: ${filePath}`);
      }
    }
  } catch (error) {
    console.error('Error deleting media file:', error);
  }
}

async function processSelfDestructMessages() {
  try {
    const now = new Date();

    const messagesToDelete = await Chat.find({
      selfDestruct: true,
      viewed: true,
      deleteAt: { $lte: now },
      isDeleted: false,
    }).limit(BATCH_SIZE).lean();

    for (const message of messagesToDelete) {
      try {
        if (message.mediaUrl) {
          await deleteMediaFile(message.mediaUrl);
        }

        await Chat.findByIdAndUpdate(message._id, {
          isDeleted: true,
          encryptedMessage: '[Message deleted]',
          mediaUrl: null,
        });

        if (ioInstance && userSocketsRef) {
          const senderSocketId = userSocketsRef.get(message.senderId);
          const receiverSocketId = userSocketsRef.get(message.receiverId);

          const deleteEvent = {
            messageId: message._id.toString(),
            chatRoomId: message.chatRoomId,
            reason: 'self_destruct',
          };

          if (senderSocketId) {
            ioInstance.to(senderSocketId).emit('message-self-destructed', deleteEvent);
          }
          if (receiverSocketId) {
            ioInstance.to(receiverSocketId).emit('message-self-destructed', deleteEvent);
          }
        }

        console.log(`ðŸ”¥ Self-destructed message: ${message._id}`);
      } catch (error) {
        console.error(`Error deleting message ${message._id}:`, error);
      }
    }

    return messagesToDelete.length;
  } catch (error) {
    console.error('Error processing self-destruct messages:', error);
    return 0;
  }
}

async function processGhostMessages() {
  try {
    const now = new Date();

    const messagesToDelete = await GhostMessage.find({
      viewed: true,
      deleteAt: { $lte: now },
      isDeleted: false,
    }).limit(BATCH_SIZE).lean();

    for (const message of messagesToDelete) {
      try {
        if (message.encryptedMediaUrl) {
          await deleteMediaFile(message.encryptedMediaUrl);
        }

        await GhostMessage.findByIdAndUpdate(message._id, {
          isDeleted: true,
          encryptedPayload: '[Ghost message deleted]',
          encryptedMediaUrl: null,
        });

        if (ioInstance && userSocketsRef) {
          const senderSocketId = userSocketsRef.get(message.senderId);
          const receiverSocketId = userSocketsRef.get(message.receiverId);

          const deleteEvent = {
            messageId: message._id.toString(),
            sessionId: message.sessionId,
            reason: 'ghost_auto_delete',
          };

          if (senderSocketId) {
            ioInstance.to(senderSocketId).emit('ghost-message-deleted', deleteEvent);
          }
          if (receiverSocketId) {
            ioInstance.to(receiverSocketId).emit('ghost-message-deleted', deleteEvent);
          }
        }

        console.log(`ðŸ‘» Ghost message deleted: ${message._id}`);
      } catch (error) {
        console.error(`Error deleting ghost message ${message._id}:`, error);
      }
    }

    return messagesToDelete.length;
  } catch (error) {
    console.error('Error processing ghost messages:', error);
    return 0;
  }
}

async function processAudioPlayback() {
  try {
    const now = new Date();

    const audioMessages = await Chat.find({
      messageType: 'audio',
      selfDestruct: true,
      playTimestamp: { $ne: null },
      deleteAt: null,
      isDeleted: false,
    }).limit(BATCH_SIZE);

    for (const message of audioMessages) {
      const playTime = new Date(message.playTimestamp);
      const deleteTime = new Date(playTime.getTime() + (message.autoDeleteTimer || 30) * 1000);

      if (deleteTime <= now) {
        if (message.mediaUrl) {
          await deleteMediaFile(message.mediaUrl);
        }

        await Chat.findByIdAndUpdate(message._id, {
          isDeleted: true,
          encryptedMessage: '[Audio deleted]',
          mediaUrl: null,
        });

        if (ioInstance && userSocketsRef) {
          const senderSocketId = userSocketsRef.get(message.senderId);
          const receiverSocketId = userSocketsRef.get(message.receiverId);

          const deleteEvent = {
            messageId: message._id.toString(),
            chatRoomId: message.chatRoomId,
            reason: 'audio_playback_complete',
          };

          if (senderSocketId) {
            ioInstance.to(senderSocketId).emit('message-self-destructed', deleteEvent);
          }
          if (receiverSocketId) {
            ioInstance.to(receiverSocketId).emit('message-self-destructed', deleteEvent);
          }
        }

        console.log(`ðŸŽµ Audio message deleted after playback: ${message._id}`);
      }
    }
  } catch (error) {
    console.error('Error processing audio playback deletions:', error);
  }
}

async function runDeletionWorker() {
  if (!deletionWorkerRunning) return;

  try {
    const regularDeleted = await processSelfDestructMessages();
    const ghostDeleted = await processGhostMessages();
    await processAudioPlayback();

    if (regularDeleted > 0 || ghostDeleted > 0) {
      console.log(`ðŸ—‘ï¸ Deletion worker: ${regularDeleted} regular, ${ghostDeleted} ghost messages processed`);
    }
  } catch (error) {
    console.error('Deletion worker error:', error);
  }
}

export function startDeletionWorker(io, userSockets) {
  if (deletionWorkerRunning) {
    console.log('Deletion worker already running');
    return;
  }

  ioInstance = io;
  userSocketsRef = userSockets;
  deletionWorkerRunning = true;

  deletionInterval = setInterval(runDeletionWorker, DELETION_INTERVAL);

  console.log('ðŸ—‘ï¸ Deletion worker started');
}

export function stopDeletionWorker() {
  if (deletionInterval) {
    clearInterval(deletionInterval);
    deletionInterval = null;
  }
  deletionWorkerRunning = false;
  console.log('ðŸ—‘ï¸ Deletion worker stopped');
}

export async function scheduleMessageDeletion(messageId, deleteAfterSeconds) {
  try {
    const deleteAt = new Date(Date.now() + deleteAfterSeconds * 1000);
    await Chat.findByIdAndUpdate(messageId, {
      deleteAt,
      selfDestruct: true,
    });
    console.log(`â±ï¸ Message ${messageId} scheduled for deletion at ${deleteAt}`);
    return true;
  } catch (error) {
    console.error('Error scheduling message deletion:', error);
    return false;
  }
}

export async function markMessageViewed(messageId, isGhost = false) {
  try {
    const Model = isGhost ? GhostMessage : Chat;
    const message = await Model.findById(messageId);

    if (!message || message.viewed) return null;

    const viewTimestamp = new Date();
    const autoDeleteTimer = message.autoDeleteTimer || 30;
    const deleteAt = new Date(viewTimestamp.getTime() + autoDeleteTimer * 1000);

    await Model.findByIdAndUpdate(messageId, {
      viewed: true,
      viewTimestamp,
      deleteAt,
    });

    console.log(`ðŸ‘ï¸ Message ${messageId} marked as viewed, deleting at ${deleteAt}`);

    return {
      viewTimestamp,
      deleteAt,
      autoDeleteTimer,
    };
  } catch (error) {
    console.error('Error marking message as viewed:', error);
    return null;
  }
}

export async function markAudioPlayed(messageId) {
  try {
    const message = await Chat.findById(messageId);

    if (!message || message.playTimestamp) return null;

    const playTimestamp = new Date();
    const autoDeleteTimer = message.autoDeleteTimer || 30;
    const deleteAt = new Date(playTimestamp.getTime() + autoDeleteTimer * 1000);

    await Chat.findByIdAndUpdate(messageId, {
      playTimestamp,
      deleteAt,
    });

    console.log(`ðŸŽµ Audio ${messageId} started playing, deleting at ${deleteAt}`);

    return {
      playTimestamp,
      deleteAt,
      autoDeleteTimer,
    };
  } catch (error) {
    console.error('Error marking audio as played:', error);
    return null;
  }
}

export default {
  startDeletionWorker,
  stopDeletionWorker,
  scheduleMessageDeletion,
  markMessageViewed,
  markAudioPlayed,
};

client/src/hooks/usePrivacyProtection.ts.content:
import { useEffect, useCallback, useState, useRef } from 'react';

interface UsePrivacyProtectionOptions {
  onScreenshotDetected?: () => void;
  onVisibilityChange?: (isVisible: boolean) => void;
  enableBlurOnSwitch?: boolean;
}

export function usePrivacyProtection(options: UsePrivacyProtectionOptions = {}) {
  const [isBlurred, setIsBlurred] = useState(false);
  const [screenshotCount, setScreenshotCount] = useState(0);
  const lastKeyPressRef = useRef<number>(0);

  const handleVisibilityChange = useCallback(() => {
    const isVisible = document.visibilityState === 'visible';

    if (options.enableBlurOnSwitch) {
      setIsBlurred(!isVisible);
    }

    options.onVisibilityChange?.(isVisible);
  }, [options]);

  const detectScreenshotKeyCombo = useCallback((e: KeyboardEvent) => {
    const now = Date.now();
    const isPrintScreen = e.key === 'PrintScreen';
    const isScreenshotCombo = 
      (e.metaKey && e.shiftKey && (e.key === '3' || e.key === '4' || e.key === '5')) || // macOS
      (e.ctrlKey && e.key === 'PrintScreen') || // Windows Snipping
      isPrintScreen;

    if (isScreenshotCombo) {
      if (now - lastKeyPressRef.current > 1000) {
        lastKeyPressRef.current = now;
        setScreenshotCount(prev => prev + 1);
        options.onScreenshotDetected?.();
        console.log('ðŸ“¸ Screenshot attempt detected');
      }
    }
  }, [options]);

  useEffect(() => {
    document.addEventListener('visibilitychange', handleVisibilityChange);
    window.addEventListener('keydown', detectScreenshotKeyCombo);
    window.addEventListener('keyup', detectScreenshotKeyCombo);

    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
      window.removeEventListener('keydown', detectScreenshotKeyCombo);
      window.removeEventListener('keyup', detectScreenshotKeyCombo);
    };
  }, [handleVisibilityChange, detectScreenshotKeyCombo]);

  const enableBlur = useCallback(() => {
    setIsBlurred(true);
  }, []);

  const disableBlur = useCallback(() => {
    setIsBlurred(false);
  }, []);

  return {
    isBlurred,
    screenshotCount,
    enableBlur,
    disableBlur,
  };
}

export function PrivacyBlurOverlay({ isBlurred }: { isBlurred: boolean }) {
  if (!isBlurred) return null;

  return (
    <div 
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.95)',
        backdropFilter: 'blur(20px)',
        zIndex: 9999,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#00ffff',
        fontSize: '1.5rem',
        fontFamily: 'monospace',
      }}
    >
      <div style={{ textAlign: 'center' }}>
        <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>ðŸ”’</div>
        <div>Content Hidden</div>
        <div style={{ fontSize: '0.875rem', opacity: 0.7, marginTop: '0.5rem' }}>
          Return to app to view
        </div>
      </div>
    </div>
  );
}

export default usePrivacyProtection;

client/src/hooks/useSelfDestruct.ts.content:
import { useState, useEffect, useCallback, useRef } from 'react';
import { Socket } from 'socket.io-client';

interface SelfDestructMessage {
  messageId: string;
  viewTimestamp: Date | null;
  deleteAt: Date | null;
  autoDeleteTimer: number;
  remainingTime: number;
}

interface UseSelfDestructOptions {
  socket: Socket | null;
  onMessageDeleted?: (messageId: string) => void;
  onScreenshotAlert?: (from: string) => void;
}

export function useSelfDestruct(options: UseSelfDestructOptions) {
  const { socket } = options;
  const [activeTimers, setActiveTimers] = useState<Map<string, SelfDestructMessage>>(new Map());
  const intervalRefs = useRef<Map<string, NodeJS.Timeout>>(new Map());

  const sendSelfDestructMessage = useCallback((
    to: string,
    message: string,
    autoDeleteTimer: number = 30,
    messageType: string = 'text',
    mediaUrl: string | null = null
  ) => {
    if (!socket) return;

    socket.emit('send-self-destruct-message', {
      to,
      message,
      messageType,
      mediaUrl,
      autoDeleteTimer,
    });
  }, [socket]);

  const markMessageViewed = useCallback((messageId: string) => {
    if (!socket) return;
    socket.emit('message-viewed', { messageId });
  }, [socket]);

  const markAudioPlayed = useCallback((messageId: string) => {
    if (!socket) return;
    socket.emit('audio-played', { messageId });
  }, [socket]);

  const reportScreenshot = useCallback((chatRoomId: string) => {
    if (!socket) return;
    socket.emit('screenshot-detected', { chatRoomId });
  }, [socket]);

  const startTimer = useCallback((messageId: string, deleteAt: Date, autoDeleteTimer: number) => {
    const existingInterval = intervalRefs.current.get(messageId);
    if (existingInterval) {
      clearInterval(existingInterval);
    }

    const updateTimer = () => {
      const now = new Date().getTime();
      const deleteTime = new Date(deleteAt).getTime();
      const remaining = Math.max(0, Math.ceil((deleteTime - now) / 1000));

      if (remaining <= 0) {
        const interval = intervalRefs.current.get(messageId);
        if (interval) {
          clearInterval(interval);
          intervalRefs.current.delete(messageId);
        }
        setActiveTimers(prev => {
          const newMap = new Map(prev);
          newMap.delete(messageId);
          return newMap;
        });
        options.onMessageDeleted?.(messageId);
      } else {
        setActiveTimers(prev => {
          const newMap = new Map(prev);
          const existing = newMap.get(messageId);
          if (existing) {
            newMap.set(messageId, { ...existing, remainingTime: remaining });
          }
          return newMap;
        });
      }
    };

    setActiveTimers(prev => {
      const newMap = new Map(prev);
      newMap.set(messageId, {
        messageId,
        viewTimestamp: new Date(),
        deleteAt,
        autoDeleteTimer,
        remainingTime: autoDeleteTimer,
      });
      return newMap;
    });

    const interval = setInterval(updateTimer, 1000);
    intervalRefs.current.set(messageId, interval);
    updateTimer();
  }, [options]);

  useEffect(() => {
    if (!socket) return;

    const handleViewStarted = (data: { 
      messageId: string; 
      viewTimestamp: string; 
      deleteAt: string; 
      autoDeleteTimer: number;
    }) => {
      startTimer(data.messageId, new Date(data.deleteAt), data.autoDeleteTimer);
    };

    const handleAudioPlayStarted = (data: { 
      messageId: string; 
      playTimestamp: string; 
      deleteAt: string; 
      autoDeleteTimer: number;
    }) => {
      startTimer(data.messageId, new Date(data.deleteAt), data.autoDeleteTimer);
    };

    const handleMessageDeleted = (data: { messageId: string; reason: string }) => {
      const interval = intervalRefs.current.get(data.messageId);
      if (interval) {
        clearInterval(interval);
        intervalRefs.current.delete(data.messageId);
      }
      setActiveTimers(prev => {
        const newMap = new Map(prev);
        newMap.delete(data.messageId);
        return newMap;
      });
      options.onMessageDeleted?.(data.messageId);
    };

    const handleScreenshotAlert = (data: { from: string; chatRoomId: string; timestamp: string }) => {
      options.onScreenshotAlert?.(data.from);
    };

    socket.on('message-view-started', handleViewStarted);
    socket.on('audio-play-started', handleAudioPlayStarted);
    socket.on('message-self-destructed', handleMessageDeleted);
    socket.on('screenshot-alert', handleScreenshotAlert);

    return () => {
      socket.off('message-view-started', handleViewStarted);
      socket.off('audio-play-started', handleAudioPlayStarted);
      socket.off('message-self-destructed', handleMessageDeleted);
      socket.off('screenshot-alert', handleScreenshotAlert);
    };
  }, [socket, startTimer, options]);

  useEffect(() => {
    return () => {
      intervalRefs.current.forEach(interval => clearInterval(interval));
      intervalRefs.current.clear();
    };
  }, []);

  const getTimerForMessage = useCallback((messageId: string) => {
    return activeTimers.get(messageId);
  }, [activeTimers]);

  return {
    sendSelfDestructMessage,
    markMessageViewed,
    markAudioPlayed,
    reportScreenshot,
    activeTimers,
    getTimerForMessage,
  };
}

export default useSelfDestruct;

client/src/hooks/useGhostMode.ts.content:
import { useState, useCallback, useEffect, useRef } from 'react';
import { Socket } from 'socket.io-client';

interface GhostSession {
  sessionId: string;
  participants: string[];
  sessionKey: string;
  expireAt: string;
}

interface GhostSessionStatus {
  hasSession: boolean;
  sessionId: string | null;
  ghostEnabled: boolean;
  ghostTerminated: boolean;
  hasJoined: boolean;
  isCreator: boolean;
  canEnterDirectly: boolean;
  needsPin: boolean;
  participants: string[];
  sessionKey: string | null;
}

interface GhostMessage {
  id: string;
  senderId: string;
  receiverId: string;
  content: string;
  messageType: string;
  mediaUrl: string | null;
  viewed: boolean;
  viewTimestamp: string | null;
  autoDeleteTimer: number;
  deleteAt: string | null;
  timestamp: string;
}

interface UseGhostModeOptions {
  socket: Socket | null;
  onSessionExpired?: () => void;
  onAutoLock?: () => void;
  onNewMessage?: (message: GhostMessage) => void;
}

export function useGhostMode(options: UseGhostModeOptions) {
  const { socket } = options;
  const [isGhostModeActive, setIsGhostModeActive] = useState(false);
  const [isSetUp, setIsSetUp] = useState(false);
  const [sessionToken, setSessionToken] = useState<string | null>(null);
  const [currentSession, setCurrentSession] = useState<GhostSession | null>(null);
  const [messages, setMessages] = useState<GhostMessage[]>([]);
  const [sessions, setSessions] = useState<GhostSession[]>([]);
  const [autoLockTimeout, setAutoLockTimeout] = useState(30);
  const [isLoading, setIsLoading] = useState(false);

  const idleTimerRef = useRef<NodeJS.Timeout | null>(null);
  const heartbeatRef = useRef<NodeJS.Timeout | null>(null);
  const lastActivityRef = useRef<number>(Date.now());

  const resetIdleTimer = useCallback(() => {
    lastActivityRef.current = Date.now();

    if (idleTimerRef.current) {
      clearTimeout(idleTimerRef.current);
    }

    if (isGhostModeActive && autoLockTimeout > 0) {
      idleTimerRef.current = setTimeout(() => {
        console.log('ðŸ‘» Auto-locking ghost mode due to inactivity');
        logout();
        options.onAutoLock?.();
      }, autoLockTimeout * 1000);
    }
  }, [isGhostModeActive, autoLockTimeout, options]);

  const checkGhostStatus = useCallback(async (): Promise<boolean> => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await fetch('/api/ghost/status', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        setIsSetUp(data.isSetUp);
        setAutoLockTimeout(data.autoLockTimeout || 30);
        return data.isSetUp;
      }
      return false;
    } catch (error) {
      console.error('Ghost status check error:', error);
      return false;
    }
  }, []);

  const setupGhostMode = useCallback(async (pin: string): Promise<boolean> => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('authToken');
      const response = await fetch('/api/ghost/setup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ pin }),
      });

      if (response.ok) {
        setIsSetUp(true);
        return true;
      }
      return false;
    } catch (error) {
      console.error('Ghost setup error:', error);
      return false;
    } finally {
      setIsLoading(false);
    }
  }, []);

  const authenticate = useCallback(async (pin: string, biometricToken?: string): Promise<boolean> => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('authToken');
      const response = await fetch('/api/ghost/authenticate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ pin, biometricToken }),
      });

      if (response.ok) {
        const data = await response.json();
        setSessionToken(data.sessionToken);
        setAutoLockTimeout(data.autoLockTimeout || 30);
        setIsGhostModeActive(true);
        localStorage.setItem('ghostSessionToken', data.sessionToken);
        resetIdleTimer();
        return true;
      }
      return false;
    } catch (error) {
      console.error('Ghost authentication error:', error);
      return false;
    } finally {
      setIsLoading(false);
    }
  }, [resetIdleTimer]);

  const logout = useCallback(async () => {
    try {
      const token = localStorage.getItem('authToken');
      await fetch('/api/ghost/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });
    } catch (error) {
      console.error('Ghost logout error:', error);
    } finally {
      setIsGhostModeActive(false);
      setSessionToken(null);
      setCurrentSession(null);
      setMessages([]);
      localStorage.removeItem('ghostSessionToken');

      if (idleTimerRef.current) {
        clearTimeout(idleTimerRef.current);
      }
      if (heartbeatRef.current) {
        clearInterval(heartbeatRef.current);
      }
    }
  }, []);

  const fetchSessions = useCallback(async (): Promise<GhostSession[]> => {
    if (!sessionToken) return [];

    try {
      const token = localStorage.getItem('authToken');
      const response = await fetch(`/api/ghost/sessions?sessionToken=${sessionToken}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        setSessions(data.sessions);
        return data.sessions;
      }
      return [];
    } catch (error) {
      console.error('Fetch ghost sessions error:', error);
      return [];
    }
  }, [sessionToken]);

  const createSession = useCallback(async (participantUsername: string): Promise<GhostSession | null> => {
    if (!sessionToken) return null;

    try {
      setIsLoading(true);
      const token = localStorage.getItem('authToken');
      const response = await fetch('/api/ghost/sessions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ sessionToken, participantUsername }),
      });

      if (response.ok) {
        const data = await response.json();
        setCurrentSession(data.session);
        await fetchSessions();
        return data.session;
      }
      return null;
    } catch (error) {
      console.error('Create ghost session error:', error);
      return null;
    } finally {
      setIsLoading(false);
    }
  }, [sessionToken, fetchSessions]);

  const fetchMessages = useCallback(async (sessionId: string): Promise<GhostMessage[]> => {
    if (!sessionToken) return [];

    try {
      const token = localStorage.getItem('authToken');
      const response = await fetch(`/api/ghost/messages/${sessionId}?sessionToken=${sessionToken}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        setMessages(data.messages);
        return data.messages;
      }
      return [];
    } catch (error) {
      console.error('Fetch ghost messages error:', error);
      return [];
    }
  }, [sessionToken]);

  const sendMessage = useCallback((sessionId: string, message: string, messageType: string = 'text') => {
    if (!socket || !sessionToken) return;

    resetIdleTimer();
    socket.emit('ghost-send-message', {
      sessionToken,
      sessionId,
      message,
      messageType,
    });
  }, [socket, sessionToken, resetIdleTimer]);

  const markMessageViewed = useCallback(async (messageId: string): Promise<void> => {
    if (!sessionToken) return;

    try {
      const token = localStorage.getItem('authToken');
      await fetch(`/api/ghost/messages/${messageId}/view`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ sessionToken }),
      });
    } catch (error) {
      console.error('Mark ghost message viewed error:', error);
    }
  }, [sessionToken]);

  const activateWithPartner = useCallback(async (partnerId: string, deviceType: string = 'desktop', disclaimerAgreed: boolean = false): Promise<{ pin: string; sessionId: string } | null> => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('authToken');
      const response = await fetch('/api/ghost/activate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ partnerId, deviceType, disclaimerAgreed }),
      });

      if (response.ok) {
        const data = await response.json();

        if (socket) {
          socket.emit('ghost-mode-activated', {
            partnerId,
            pin: data.pin,
            sessionId: data.sessionId,
          });
        }

        setCurrentSession({
          sessionId: data.sessionId,
          participants: [partnerId],
          sessionKey: '',
          expireAt: data.expireAt,
        });
        setIsGhostModeActive(true);

        return { pin: data.pin, sessionId: data.sessionId };
      }

      const errorData = await response.json();
      throw new Error(errorData.error || 'Activation failed');
    } catch (error: any) {
      console.error('Ghost activate error:', error);
      throw error;
    } finally {
      setIsLoading(false);
    }
  }, [socket]);

  const joinWithPin = useCallback(async (pin: string, deviceType: string = 'desktop'): Promise<boolean> => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('authToken');
      const response = await fetch('/api/ghost/join', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ pin, deviceType }),
      });

      if (response.ok) {
        const data = await response.json();

        setCurrentSession({
          sessionId: data.sessionId,
          participants: data.participants,
          sessionKey: data.sessionKey,
          expireAt: data.expireAt,
        });
        setIsGhostModeActive(true);

        if (socket) {
          socket.emit('ghost-partner-joined', {
            sessionId: data.sessionId,
            partnerId: data.partnerId,
          });
        }

        return true;
      }
      return false;
    } catch (error) {
      console.error('Ghost join error:', error);
      return false;
    } finally {
      setIsLoading(false);
    }
  }, [socket]);

  const reauthenticate = useCallback(async (sessionId: string, pin: string, deviceType: string = 'desktop'): Promise<boolean> => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('authToken');
      const response = await fetch('/api/ghost/reauth', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ sessionId, pin, deviceType }),
      });

      if (response.ok) {
        resetIdleTimer();
        return true;
      }
      return false;
    } catch (error) {
      console.error('Ghost reauth error:', error);
      return false;
    } finally {
      setIsLoading(false);
    }
  }, [resetIdleTimer]);

  const logSecurityEvent = useCallback(async (sessionId: string, eventType: string, deviceType: string = 'desktop') => {
    if (socket) {
      socket.emit('ghost-security-event', {
        sessionId,
        eventType,
        deviceType,
      });
    }
  }, [socket]);

  const terminateSession = useCallback(async (sessionId: string, partnerId?: string): Promise<boolean> => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await fetch('/api/ghost/terminate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ sessionId }),
      });

      if (response.ok) {
        const data = await response.json();

        if (socket && partnerId) {
          socket.emit('ghost-mode-terminated', {
            sessionId,
            partnerId: partnerId || data.partnerId,
          });
        }

        setCurrentSession(null);
        setMessages([]);
        setIsGhostModeActive(false);
        return true;
      }
      return false;
    } catch (error) {
      console.error('Ghost terminate error:', error);
      return false;
    }
  }, [socket]);

  const checkSessionStatus = useCallback(async (partnerId: string): Promise<GhostSessionStatus | null> => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await fetch(`/api/ghost/session-status/${partnerId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        return await response.json();
      }
      return null;
    } catch (error) {
      console.error('Ghost session status error:', error);
      return null;
    }
  }, []);

  const directEnter = useCallback(async (partnerId: string, deviceType: string = 'desktop'): Promise<GhostSession | null> => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('authToken');
      const response = await fetch('/api/ghost/direct-enter', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ partnerId, deviceType }),
      });

      if (response.ok) {
        const data = await response.json();

        const session: GhostSession = {
          sessionId: data.sessionId,
          participants: data.participants,
          sessionKey: data.sessionKey,
          expireAt: data.expireAt,
        };

        setCurrentSession(session);
        setIsGhostModeActive(true);

        if (socket) {
          socket.emit('ghost-mode-entered', {
            sessionId: data.sessionId,
            partnerId,
          });
        }

        return session;
      }
      return null;
    } catch (error) {
      console.error('Ghost direct enter error:', error);
      return null;
    } finally {
      setIsLoading(false);
    }
  }, [socket]);

  useEffect(() => {
    if (!socket || !isGhostModeActive) return;

    const handleNewMessage = (data: any) => {
      const newMessage: GhostMessage = {
        id: data.messageId,
        senderId: data.senderId,
        receiverId: '',
        content: data.content,
        messageType: data.messageType,
        mediaUrl: null,
        viewed: false,
        viewTimestamp: null,
        autoDeleteTimer: data.autoDeleteTimer,
        deleteAt: null,
        timestamp: data.timestamp,
      };

      setMessages(prev => [...prev, newMessage]);
      options.onNewMessage?.(newMessage);
    };

    const handleMessageDeleted = (data: { messageId: string }) => {
      setMessages(prev => prev.filter(m => m.id !== data.messageId));
    };

    const handleViewStarted = (data: { messageId: string; deleteAt: string }) => {
      setMessages(prev => prev.map(m => 
        m.id === data.messageId 
          ? { ...m, viewed: true, viewTimestamp: new Date().toISOString(), deleteAt: data.deleteAt }
          : m
      ));
    };

    const handleSessionTerminated = (data: { sessionId: string; terminatedBy: string }) => {
      console.log(`ðŸ‘» Ghost session terminated by ${data.terminatedBy}`);
      setCurrentSession(null);
      setMessages([]);
      setIsGhostModeActive(false);
      options.onSessionExpired?.();
    };

    socket.on('ghost-receive-message', handleNewMessage);
    socket.on('ghost-message-deleted', handleMessageDeleted);
    socket.on('ghost-message-view-started', handleViewStarted);
    socket.on('ghost-session-terminated', handleSessionTerminated);

    return () => {
      socket.off('ghost-receive-message', handleNewMessage);
      socket.off('ghost-message-deleted', handleMessageDeleted);
      socket.off('ghost-message-view-started', handleViewStarted);
      socket.off('ghost-session-terminated', handleSessionTerminated);
    };
  }, [socket, isGhostModeActive, options]);

  useEffect(() => {
    if (isGhostModeActive && sessionToken) {
      heartbeatRef.current = setInterval(async () => {
        try {
          const token = localStorage.getItem('authToken');
          const response = await fetch('/api/ghost/heartbeat', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ sessionToken }),
          });

          if (!response.ok) {
            console.log('ðŸ‘» Ghost session expired');
            logout();
            options.onSessionExpired?.();
          }
        } catch (error) {
          console.error('Ghost heartbeat error:', error);
        }
      }, 60000);
    }

    return () => {
      if (heartbeatRef.current) {
        clearInterval(heartbeatRef.current);
      }
    };
  }, [isGhostModeActive, sessionToken, logout, options]);

  useEffect(() => {
    return () => {
      if (idleTimerRef.current) clearTimeout(idleTimerRef.current);
      if (heartbeatRef.current) clearInterval(heartbeatRef.current);
    };
  }, []);

  return {
    isGhostModeActive,
    isSetUp,
    isLoading,
    sessionToken,
    currentSession,
    messages,
    sessions,
    autoLockTimeout,
    checkGhostStatus,
    setupGhostMode,
    authenticate,
    logout,
    fetchSessions,
    createSession,
    fetchMessages,
    sendMessage,
    markMessageViewed,
    resetIdleTimer,
    setCurrentSession,
    activateWithPartner,
    joinWithPin,
    reauthenticate,
    logSecurityEvent,
    terminateSession,
    checkSessionStatus,
    directEnter,
    setIsGhostModeActive,
  };
}

export default useGhostMode;

