import React, { useState, useRef, useCallback, useEffect } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Ghost, Lock, Eye, EyeOff, Shield, Fingerprint } from 'lucide-react';

interface GhostModeActivatorProps {
  onActivate: (pin: string) => Promise<boolean>;
  onSetup: (pin: string) => Promise<boolean>;
  isSetUp: boolean;
  isLoading: boolean;
}

const GESTURE_CODES = {
  LONG_PRESS_DURATION: 2000,
  SHAKE_THRESHOLD: 15,
  SHAKE_COUNT: 3,
};

export function GhostModeActivator({ 
  onActivate, 
  onSetup, 
  isSetUp, 
  isLoading 
}: GhostModeActivatorProps) {
  const [showDialog, setShowDialog] = useState(false);
  const [pin, setPin] = useState('');
  const [confirmPin, setConfirmPin] = useState('');
  const [showPin, setShowPin] = useState(false);
  const [error, setError] = useState('');
  const [isSettingUp, setIsSettingUp] = useState(!isSetUp);

  const longPressTimerRef = useRef<NodeJS.Timeout | null>(null);
  const shakeCountRef = useRef(0);
  const lastShakeRef = useRef(0);

  const handleLongPressStart = useCallback(() => {
    longPressTimerRef.current = setTimeout(() => {
      setShowDialog(true);
      setIsSettingUp(!isSetUp);
    }, GESTURE_CODES.LONG_PRESS_DURATION);
  }, [isSetUp]);

  const handleLongPressEnd = useCallback(() => {
    if (longPressTimerRef.current) {
      clearTimeout(longPressTimerRef.current);
    }
  }, []);

  useEffect(() => {
    const handleDeviceMotion = (event: DeviceMotionEvent) => {
      const acceleration = event.accelerationIncludingGravity;
      if (!acceleration) return;

      const totalAcceleration = Math.sqrt(
        (acceleration.x || 0) ** 2 +
        (acceleration.y || 0) ** 2 +
        (acceleration.z || 0) ** 2
      );

      const now = Date.now();
      if (totalAcceleration > GESTURE_CODES.SHAKE_THRESHOLD) {
        if (now - lastShakeRef.current > 500) {
          shakeCountRef.current++;
          lastShakeRef.current = now;

          if (shakeCountRef.current >= GESTURE_CODES.SHAKE_COUNT) {
            setShowDialog(true);
            setIsSettingUp(!isSetUp);
            shakeCountRef.current = 0;
          }
        }
      }

      if (now - lastShakeRef.current > 2000) {
        shakeCountRef.current = 0;
      }
    };

    if (typeof window !== 'undefined' && 'DeviceMotionEvent' in window) {
      window.addEventListener('devicemotion', handleDeviceMotion);
    }

    return () => {
      if (typeof window !== 'undefined' && 'DeviceMotionEvent' in window) {
        window.removeEventListener('devicemotion', handleDeviceMotion);
      }
    };
  }, [isSetUp]);

  const handleSubmit = async () => {
    setError('');

    if (isSettingUp) {
      if (pin.length < 4 || pin.length > 8) {
        setError('PIN must be 4-8 digits');
        return;
      }
      if (pin !== confirmPin) {
        setError('PINs do not match');
        return;
      }

      const success = await onSetup(pin);
      if (success) {
        setShowDialog(false);
        setPin('');
        setConfirmPin('');
        setIsSettingUp(false);
      } else {
        setError('Failed to set up Ghost Mode');
      }
    } else {
      const success = await onActivate(pin);
      if (success) {
        setShowDialog(false);
        setPin('');
      } else {
        setError('Invalid PIN');
      }
    }
  };

  const handleClose = () => {
    setShowDialog(false);
    setPin('');
    setConfirmPin('');
    setError('');
  };

  return (
    <>
      <div
        className="ghost-activator-trigger"
        onMouseDown={handleLongPressStart}
        onMouseUp={handleLongPressEnd}
        onMouseLeave={handleLongPressEnd}
        onTouchStart={handleLongPressStart}
        onTouchEnd={handleLongPressEnd}
        style={{ display: 'none' }}
      />

      <Dialog open={showDialog} onOpenChange={handleClose}>
        <DialogContent className="bg-gray-900 border-cyan-500/30 text-white max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2 text-cyan-400">
              <Ghost className="w-6 h-6" />
              {isSettingUp ? 'Set Up Ghost Mode' : 'Enter Ghost Mode'}
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-6 py-4">
            <div className="text-center">
              <div className="w-20 h-20 mx-auto mb-4 rounded-full bg-gradient-to-br from-cyan-500/20 to-purple-500/20 flex items-center justify-center border border-cyan-500/30">
                <Shield className="w-10 h-10 text-cyan-400" />
              </div>
              <p className="text-gray-400 text-sm">
                {isSettingUp 
                  ? 'Create a secure PIN to protect your ghost conversations'
                  : 'Enter your PIN to access hidden conversations'
                }
              </p>
            </div>

            <div className="space-y-4">
              <div className="relative">
                <Input
                  type={showPin ? 'text' : 'password'}
                  placeholder="Enter PIN (4-8 digits)"
                  value={pin}
                  onChange={(e) => setPin(e.target.value.replace(/\D/g, '').slice(0, 8))}
                  className="bg-gray-800 border-gray-700 text-white text-center text-xl tracking-widest pr-10"
                  maxLength={8}
                />
                <button
                  type="button"
                  onClick={() => setShowPin(!showPin)}
                  className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400 hover:text-white"
                >
                  {showPin ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                </button>
              </div>

              {isSettingUp && (
                <div className="relative">
                  <Input
                    type={showPin ? 'text' : 'password'}
                    placeholder="Confirm PIN"
                    value={confirmPin}
                    onChange={(e) => setConfirmPin(e.target.value.replace(/\D/g, '').slice(0, 8))}
                    className="bg-gray-800 border-gray-700 text-white text-center text-xl tracking-widest"
                    maxLength={8}
                  />
                </div>
              )}

              {error && (
                <p className="text-red-400 text-sm text-center">{error}</p>
              )}

              <Button
                onClick={handleSubmit}
                disabled={isLoading || pin.length < 4}
                className="w-full bg-gradient-to-r from-cyan-600 to-purple-600 hover:from-cyan-500 hover:to-purple-500"
              >
                {isLoading ? (
                  <span className="flex items-center gap-2">
                    <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                    {isSettingUp ? 'Setting up...' : 'Verifying...'}
                  </span>
                ) : (
                  <span className="flex items-center gap-2">
                    <Lock className="w-4 h-4" />
                    {isSettingUp ? 'Set Up Ghost Mode' : 'Enter Ghost Mode'}
                  </span>
                )}
              </Button>
            </div>

            <div className="flex items-center justify-center gap-2 text-gray-500 text-xs">
              <Fingerprint className="w-4 h-4" />
              <span>Biometric authentication available on supported devices</span>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

export function GhostModeButton({ onClick }: { onClick: () => void }) {
  return (
    <button
      onClick={onClick}
      className="fixed bottom-4 right-4 w-12 h-12 rounded-full bg-gray-900/80 border border-cyan-500/30 flex items-center justify-center text-cyan-400 hover:bg-gray-800 hover:border-cyan-400 transition-all duration-300 shadow-lg shadow-cyan-500/10 z-50"
      style={{ 
        opacity: 0.3,
        transition: 'opacity 0.3s ease',
      }}
      onMouseEnter={(e) => e.currentTarget.style.opacity = '1'}
      onMouseLeave={(e) => e.currentTarget.style.opacity = '0.3'}
    >
      <Ghost className="w-6 h-6" />
    </button>
  );
}

export default GhostModeActivator;

client/src/components/ghost/GhostModeBut.content:
import React, { useState } from 'react';
import { Ghost, Lock, Unlock, Eye, EyeOff, Copy, Check, AlertTriangle, Shield } from 'lucide-react';
import { Button } from '@/components/ui/button';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from '@/components/ui/tooltip';
import { Input } from '@/components/ui/input';
import { Checkbox } from '@/components/ui/checkbox';

interface GhostSessionStatus {
  hasSession: boolean;
  sessionId: string | null;
  ghostEnabled: boolean;
  ghostTerminated: boolean;
  hasJoined: boolean;
  isCreator: boolean;
  canEnterDirectly: boolean;
  needsPin: boolean;
  participants: string[];
  sessionKey: string | null;
}

interface GhostModeButtonProps {
  partnerId: string;
  partnerName?: string;
  onActivate: (partnerId: string, deviceType: string, disclaimerAgreed: boolean) => Promise<{ pin: string; sessionId: string } | null>;
  onJoin: (pin: string, deviceType: string) => Promise<boolean>;
  onDirectEnter: (partnerId: string, deviceType: string) => Promise<any>;
  onCheckStatus: (partnerId: string) => Promise<GhostSessionStatus | null>;
  onSendMessage?: (message: string) => void;
  onEnterGhostMode?: () => void;
  className?: string;
}

export function GhostModeButton({
  partnerId,
  partnerName,
  onActivate,
  onJoin,
  onDirectEnter,
  onCheckStatus,
  onSendMessage,
  onEnterGhostMode,
  className = '',
}: GhostModeButtonProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [mode, setMode] = useState<'loading' | 'choose' | 'disclaimer' | 'success' | 'join'>('loading');
  const [pin, setPin] = useState('');
  const [generatedPin, setGeneratedPin] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showPin, setShowPin] = useState(false);
  const [disclaimerAgreed, setDisclaimerAgreed] = useState(false);
  const [copied, setCopied] = useState(false);
  const [sessionStatus, setSessionStatus] = useState<GhostSessionStatus | null>(null);

  const detectDeviceType = (): 'mobile' | 'desktop' | 'tablet' => {
    const userAgent = navigator.userAgent.toLowerCase();
    if (/tablet|ipad|playbook|silk/i.test(userAgent)) {
      return 'tablet';
    }
    if (/mobile|iphone|ipod|android|blackberry|opera mini|iemobile/i.test(userAgent)) {
      return 'mobile';
    }
    return 'desktop';
  };

  const handleGeneratePin = async () => {
    if (!disclaimerAgreed) {
      setError('You must agree to the disclaimer to continue');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const deviceType = detectDeviceType();
      const result = await onActivate(partnerId, deviceType, true);

      if (result) {
        setGeneratedPin(result.pin);
        setMode('success');

        if (onSendMessage) {
          const username = localStorage.getItem('username') || 'User';
          onSendMessage(`Ghost Mode activated by ${username}, Ghost PIN: ${result.pin}`);
        }

        onEnterGhostMode?.();
      } else {
        setError('Failed to activate Ghost Mode');
      }
    } catch (err: any) {
      setError(err.message || 'Activation failed');
    } finally {
      setLoading(false);
    }
  };

  const handleJoin = async () => {
    if (pin.length !== 6) {
      setError('Please enter a valid 6-digit PIN');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const deviceType = detectDeviceType();
      const success = await onJoin(pin, deviceType);

      if (success) {
        setIsOpen(false);
        resetState();
        onEnterGhostMode?.();
      } else {
        setError('Invalid PIN or expired invitation');
      }
    } catch (err: any) {
      setError(err.message || 'Failed to join');
    } finally {
      setLoading(false);
    }
  };

  const resetState = () => {
    setMode('loading');
    setPin('');
    setGeneratedPin('');
    setError('');
    setShowPin(false);
    setDisclaimerAgreed(false);
    setCopied(false);
    setSessionStatus(null);
  };

  const checkStatus = async () => {
    setMode('loading');
    try {
      const status = await onCheckStatus(partnerId);
      setSessionStatus(status);

      if (status?.canEnterDirectly) {
        setMode('choose');
      } else if (status?.needsPin) {
        setMode('choose');
      } else {
        setMode('choose');
      }
    } catch (err) {
      console.error('Failed to check ghost status:', err);
      setMode('choose');
    }
  };

  const handleOpenChange = async (open: boolean) => {
    setIsOpen(open);
    if (open) {
      await checkStatus();
    } else {
      resetState();
    }
  };

  const handleDirectEnter = async () => {
    setLoading(true);
    setError('');

    try {
      const deviceType = detectDeviceType();
      const result = await onDirectEnter(partnerId, deviceType);

      if (result) {
        setIsOpen(false);
        resetState();
        onEnterGhostMode?.();
      } else {
        setError('Failed to enter Ghost Mode');
      }
    } catch (err: any) {
      setError(err.message || 'Failed to enter Ghost Mode');
    } finally {
      setLoading(false);
    }
  };

  const copyPin = async () => {
    await navigator.clipboard.writeText(generatedPin);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleOpenChange}>
      <TooltipProvider>
        <Tooltip>
          <TooltipTrigger asChild>
            <DialogTrigger asChild>
              <Button
                variant="ghost"
                size="icon"
                className={`relative hover:bg-purple-500/20 transition-colors ${className}`}
              >
                <Ghost className="h-5 w-5 text-purple-500" />
                <span className="absolute -top-1 -right-1 w-2 h-2 bg-purple-500 rounded-full animate-pulse" />
              </Button>
            </DialogTrigger>
          </TooltipTrigger>
          <TooltipContent side="bottom">
            <p>Ghost Mode - Secret Chat</p>
          </TooltipContent>
        </Tooltip>
      </TooltipProvider>

      <DialogContent className="sm:max-w-md bg-gray-900 border-purple-500/30 text-white">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-purple-400">
            <Ghost className="h-5 w-5" />
            Ghost Mode
          </DialogTitle>
          <DialogDescription className="text-gray-400">
            {mode === 'choose' && 'Start a completely invisible encrypted chat session'}
            {mode === 'disclaimer' && 'Please read and accept the disclaimer to continue'}
            {mode === 'join' && 'Enter the PIN shared with you to join the session'}
            {mode === 'success' && 'Share this PIN with your partner to connect'}
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 py-4">
          {error && (
            <div className="p-3 bg-red-500/20 border border-red-500/30 rounded-lg text-red-400 text-sm">
              {error}
            </div>
          )}

          {mode === 'loading' && (
            <div className="flex items-center justify-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-500"></div>
            </div>
          )}

          {mode === 'choose' && (
            <div className="space-y-3">
              {sessionStatus?.canEnterDirectly ? (
                <Button
                  onClick={handleDirectEnter}
                  className="w-full bg-green-600 hover:bg-green-700 text-white"
                  disabled={loading}
                >
                  <Ghost className="h-4 w-4 mr-2" />
                  {loading ? 'Entering...' : 'Enter Ghost Mode'}
                </Button>
              ) : sessionStatus?.needsPin ? (
                <Button
                  onClick={() => setMode('join')}
                  className="w-full bg-purple-600 hover:bg-purple-700 text-white"
                >
                  <Unlock className="h-4 w-4 mr-2" />
                  Join Ghost Mode using PIN
                </Button>
              ) : (
                <Button
                  onClick={() => setMode('disclaimer')}
                  className="w-full bg-purple-600 hover:bg-purple-700 text-white"
                >
                  <Lock className="h-4 w-4 mr-2" />
                  Start Ghost Session with {partnerName || partnerId}
                </Button>
              )}

              {!sessionStatus?.canEnterDirectly && !sessionStatus?.needsPin && (
                <Button
                  onClick={() => setMode('join')}
                  variant="outline"
                  className="w-full border-purple-500/50 text-purple-400 hover:bg-purple-500/20"
                >
                  <Unlock className="h-4 w-4 mr-2" />
                  Join with PIN
                </Button>
              )}

              {sessionStatus?.canEnterDirectly && (
                <p className="text-xs text-green-400 text-center mt-2">
                  You have an active Ghost Mode session. Click above to enter directly.
                </p>
              )}

              {sessionStatus?.needsPin && (
                <p className="text-xs text-purple-400 text-center mt-2">
                  Your partner activated Ghost Mode. Enter the PIN they shared to join.
                </p>
              )}

              <p className="text-xs text-gray-500 text-center mt-4">
                Ghost Mode messages are end-to-end encrypted, auto-expire in 24 hours,
                and are hidden from your main chat history.
              </p>
            </div>
          )}

          {mode === 'disclaimer' && (
            <div className="space-y-4">
              <div className="p-4 bg-amber-500/10 rounded-lg border border-amber-500/30">
                <div className="flex items-start gap-3">
                  <AlertTriangle className="h-5 w-5 text-amber-500 mt-0.5 flex-shrink-0" />
                  <div className="space-y-2">
                    <h4 className="font-semibold text-amber-400">Confidentiality Warning</h4>
                    <p className="text-sm text-gray-300">
                      Ghost Mode provides completely invisible encrypted chat sessions. By activating Ghost Mode:
                    </p>
                    <ul className="text-sm text-gray-400 list-disc list-inside space-y-1">
                      <li>All conversations are strictly confidential</li>
                      <li>You agree NOT to share, screenshot, or disclose any Ghost Mode content to anyone</li>
                      <li>Messages auto-expire and cannot be recovered</li>
                      <li>The system generates a secure PIN that will be shared in your normal chat</li>
                    </ul>
                  </div>
                </div>
              </div>

              <div className="p-4 bg-purple-500/10 rounded-lg border border-purple-500/30">
                <div className="flex items-start gap-3">
                  <Shield className="h-5 w-5 text-purple-400 mt-0.5 flex-shrink-0" />
                  <p className="text-sm text-gray-300">
                    A secure 6-digit PIN will be auto-generated and sent as a message in your normal chat with <strong className="text-purple-400">{partnerName || partnerId}</strong>. You cannot edit this PIN - only regenerate it.
                  </p>
                </div>
              </div>

              <div className="flex items-start space-x-3 p-3 bg-gray-800/50 rounded-lg">
                <Checkbox
                  id="disclaimer"
                  checked={disclaimerAgreed}
                  onCheckedChange={(checked) => setDisclaimerAgreed(checked === true)}
                  className="mt-0.5 border-purple-500 data-[state=checked]:bg-purple-600 data-[state=checked]:border-purple-600"
                />
                <label
                  htmlFor="disclaimer"
                  className="text-sm text-gray-300 cursor-pointer leading-relaxed"
                >
                  I understand and agree that Ghost Mode conversations are strictly confidential and must NOT be shared or disclosed to anyone. I accept responsibility for maintaining the privacy of these communications.
                </label>
              </div>

              <div className="flex gap-2">
                <Button
                  onClick={() => setMode('choose')}
                  variant="outline"
                  className="flex-1 border-gray-600"
                  disabled={loading}
                >
                  Back
                </Button>
                <Button
                  onClick={handleGeneratePin}
                  className="flex-1 bg-purple-600 hover:bg-purple-700"
                  disabled={loading || !disclaimerAgreed}
                >
                  {loading ? 'Generating...' : 'Generate Ghost PIN'}
                </Button>
              </div>
            </div>
          )}

          {mode === 'join' && (
            <div className="space-y-4">
              <div className="space-y-2">
                <label className="text-sm text-gray-400">Enter 6-digit PIN</label>
                <div className="relative">
                  <Input
                    type={showPin ? 'text' : 'password'}
                    value={pin}
                    onChange={(e) => setPin(e.target.value.replace(/\D/g, '').slice(0, 6))}
                    placeholder="000000"
                    className="bg-gray-800 border-purple-500/30 text-center text-2xl tracking-[0.5em] font-mono"
                    maxLength={6}
                  />
                  <button
                    type="button"
                    onClick={() => setShowPin(!showPin)}
                    className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400 hover:text-white"
                  >
                    {showPin ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                  </button>
                </div>
              </div>
              <div className="flex gap-2">
                <Button
                  onClick={() => setMode('choose')}
                  variant="outline"
                  className="flex-1 border-gray-600"
                  disabled={loading}
                >
                  Back
                </Button>
                <Button
                  onClick={handleJoin}
                  className="flex-1 bg-purple-600 hover:bg-purple-700"
                  disabled={loading || pin.length !== 6}
                >
                  {loading ? 'Joining...' : 'Join Session'}
                </Button>
              </div>
            </div>
          )}

          {mode === 'success' && (
            <div className="space-y-4">
              <div className="p-6 bg-green-500/10 rounded-lg border border-green-500/30 text-center">
                <div className="flex items-center justify-center gap-2 text-green-400 text-sm mb-3">
                  <Check className="h-4 w-4" />
                  Ghost Mode Activated
                </div>
                <div className="relative">
                  <div className="text-4xl font-mono tracking-[0.3em] text-white bg-gray-800/50 py-4 rounded-lg flex items-center justify-center gap-3">
                    <span>{showPin ? generatedPin : '******'}</span>
                    <div className="flex items-center gap-1">
                      <button
                        type="button"
                        onClick={() => setShowPin(!showPin)}
                        className="text-gray-400 hover:text-white p-1"
                      >
                        {showPin ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
                      </button>
                      <button
                        type="button"
                        onClick={copyPin}
                        className="text-gray-400 hover:text-white p-1"
                      >
                        {copied ? <Check className="h-5 w-5 text-green-400" /> : <Copy className="h-5 w-5" />}
                      </button>
                    </div>
                  </div>
                </div>
                <p className="text-xs text-gray-400 mt-3">
                  This PIN has been automatically sent to your chat with {partnerName || partnerId}.
                  The PIN expires in 1 hour.
                </p>
              </div>
              <div className="p-3 bg-purple-500/10 rounded-lg border border-purple-500/20">
                <p className="text-xs text-gray-400 text-center">
                  Your partner can now join using this PIN by clicking the Ghost Mode button in their chat and selecting "Join with PIN".
                </p>
              </div>
              <Button
                onClick={() => setIsOpen(false)}
                className="w-full bg-purple-600 hover:bg-purple-700"
              >
                Done
              </Button>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

export default GhostModeButton;

server/models/GhostChatAccess.js.content:
import mongoose from 'mongoose';
import bcrypt from 'bcryptjs';

const ghostChatAccessSchema = new mongoose.Schema({
  sessionId: {
    type: String,
    required: true,
    index: true,
  },

  userId: {
    type: String,
    required: true,
    index: true,
  },

  partnerId: {
    type: String,
    required: true,
    index: true,
  },

  pinHash: {
    type: String,
    required: true,
  },

  deviceType: {
    type: String,
    enum: ['mobile', 'desktop', 'tablet', 'unknown'],
    default: 'desktop',
    required: true,
  },

  isActive: {
    type: Boolean,
    default: true,
  },

  accessGranted: {
    type: Boolean,
    default: false,
  },

  accessGrantedAt: {
    type: Date,
    default: null,
  },

  lastActivity: {
    type: Date,
    default: Date.now,
  },

  createdAt: {
    type: Date,
    default: Date.now,
  },

  expireAt: {
    type: Date,
    required: true,
    index: { expires: 0 },
  },
});

ghostChatAccessSchema.index({ userId: 1, partnerId: 1, sessionId: 1 });
ghostChatAccessSchema.index({ sessionId: 1, isActive: 1 });

ghostChatAccessSchema.statics.hashPin = async function(pin) {
  const salt = await bcrypt.genSalt(12);
  return bcrypt.hash(pin, salt);
};

ghostChatAccessSchema.statics.verifyPin = async function(pin, hash) {
  return bcrypt.compare(pin, hash);
};

ghostChatAccessSchema.statics.generatePin = function() {
  return Math.floor(100000 + Math.random() * 900000).toString();
};

const GhostChatAccess = mongoose.model('GhostChatAccess', ghostChatAccessSchema);

export default GhostChatAccess;

client/src/components/chat/SelfDestructM.content:
import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Clock, Flame, Eye, Volume2 } from 'lucide-react';

interface SelfDestructMessageProps {
  messageId: string;
  content: string;
  messageType: 'text' | 'audio' | 'image' | 'video';
  mediaUrl?: string | null;
  isOwn: boolean;
  autoDeleteTimer: number;
  viewed: boolean;
  viewTimestamp: string | null;
  deleteAt: string | null;
  timestamp: string;
  onView: () => void;
  onAudioPlay?: () => void;
}

export function SelfDestructMessage({
  messageId,
  content,
  messageType,
  mediaUrl,
  isOwn,
  autoDeleteTimer,
  viewed,
  viewTimestamp,
  deleteAt,
  timestamp,
  onView,
  onAudioPlay,
}: SelfDestructMessageProps) {
  const [remainingTime, setRemainingTime] = useState<number | null>(null);
  const [isRevealed, setIsRevealed] = useState(viewed);
  const [isDeleted, setIsDeleted] = useState(false);

  useEffect(() => {
    if (deleteAt) {
      const updateTimer = () => {
        const now = Date.now();
        const deleteTime = new Date(deleteAt).getTime();
        const remaining = Math.max(0, Math.ceil((deleteTime - now) / 1000));
        setRemainingTime(remaining);

        if (remaining === 0) {
          setIsDeleted(true);
        }
      };

      updateTimer();
      const interval = setInterval(updateTimer, 1000);
      return () => clearInterval(interval);
    }
  }, [deleteAt]);

  const handleReveal = () => {
    if (!isOwn && !isRevealed) {
      setIsRevealed(true);
      onView();
    }
  };

  const handleAudioPlay = () => {
    if (onAudioPlay && messageType === 'audio') {
      onAudioPlay();
    }
  };

  if (isDeleted) {
    return (
      <motion.div
        initial={{ opacity: 1, scale: 1 }}
        animate={{ opacity: 0, scale: 0.8, height: 0 }}
        transition={{ duration: 0.5 }}
        className={`flex ${isOwn ? 'justify-end' : 'justify-start'} mb-2`}
      />
    );
  }

  const getTimerColor = () => {
    if (remainingTime === null) return 'text-gray-400';
    if (remainingTime <= 5) return 'text-red-500';
    if (remainingTime <= 15) return 'text-orange-500';
    return 'text-yellow-500';
  };

  const getProgressWidth = () => {
    if (remainingTime === null || !deleteAt) return '100%';
    return `${(remainingTime / autoDeleteTimer) * 100}%`;
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      className={`flex ${isOwn ? 'justify-end' : 'justify-start'} mb-2`}
    >
      <div
        className={`relative max-w-[80%] rounded-2xl overflow-hidden ${
          isOwn
            ? 'bg-gradient-to-br from-orange-600/90 to-red-600/90'
            : 'bg-gray-800/90 border border-orange-500/30'
        }`}
      >
        {remainingTime !== null && (
          <div 
            className="absolute bottom-0 left-0 h-0.5 bg-gradient-to-r from-orange-500 to-red-500 transition-all duration-1000"
            style={{ width: getProgressWidth() }}
          />
        )}

        <div
          onClick={handleReveal}
          className="relative px-4 py-2 cursor-pointer"
        >
          {!isOwn && !isRevealed && (
            <motion.div
              initial={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="absolute inset-0 bg-gray-900/95 backdrop-blur-sm flex items-center justify-center rounded-2xl z-10"
            >
              <div className="flex items-center gap-2 text-orange-400">
                <Flame className="w-4 h-4 animate-pulse" />
                <span className="text-sm font-medium">Self-destruct message</span>
                <Eye className="w-4 h-4" />
              </div>
            </motion.div>
          )}

          {messageType === 'text' && (
            <p className="text-white text-sm relative z-0">{content}</p>
          )}

          {messageType === 'audio' && mediaUrl && (
            <div className="flex items-center gap-3">
              <button
                onClick={handleAudioPlay}
                className="w-10 h-10 rounded-full bg-orange-500/30 flex items-center justify-center hover:bg-orange-500/50 transition-colors"
              >
                <Volume2 className="w-5 h-5 text-white" />
              </button>
              <div className="flex-1">
                <div className="h-1 bg-white/20 rounded-full">
                  <div className="h-1 bg-white/60 rounded-full w-0" />
                </div>
              </div>
            </div>
          )}

          {(messageType === 'image' || messageType === 'video') && mediaUrl && (
            <div className="relative">
              {messageType === 'image' ? (
                <img 
                  src={mediaUrl} 
                  alt="Self-destruct media" 
                  className="max-w-full rounded-lg"
                />
              ) : (
                <video 
                  src={mediaUrl} 
                  controls 
                  className="max-w-full rounded-lg"
                />
              )}
            </div>
          )}

          <div className="flex items-center justify-between mt-1 text-xs">
            <span className="text-white/60">
              {new Date(timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </span>

            <div className="flex items-center gap-2">
              {remainingTime !== null && (
                <motion.span
                  key={remainingTime}
                  initial={{ scale: 1.2 }}
                  animate={{ scale: 1 }}
                  className={`flex items-center gap-1 ${getTimerColor()}`}
                >
                  <Clock className="w-3 h-3" />
                  {remainingTime}s
                </motion.span>
              )}

              {!viewed && !isOwn && (
                <span className="text-orange-400 flex items-center gap-1">
                  <Flame className="w-3 h-3" />
                </span>
              )}
            </div>
          </div>
        </div>

        <AnimatePresence>
          {remainingTime !== null && remainingTime <= 5 && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="absolute inset-0 pointer-events-none"
            >
              <div className="absolute inset-0 bg-gradient-to-t from-red-600/30 to-transparent animate-pulse" />
              {Array.from({ length: 5 }).map((_, i) => (
                <motion.div
                  key={i}
                  initial={{ y: 0, opacity: 1 }}
                  animate={{ y: -20, opacity: 0 }}
                  transition={{
                    duration: 1,
                    delay: i * 0.2,
                    repeat: Infinity,
                  }}
                  className="absolute bottom-0"
                  style={{ left: `${20 + i * 15}%` }}
                >
                  <Flame className="w-4 h-4 text-orange-500" />
                </motion.div>
              ))}
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.div>
  );
}

export function SelfDestructToggle({
  enabled,
  timer,
  onToggle,
  onTimerChange,
}: {
  enabled: boolean;
  timer: number;
  onToggle: () => void;
  onTimerChange: (seconds: number) => void;
}) {
  const timerOptions = [5, 10, 30, 60, 300];

  return (
    <div className="flex items-center gap-2 p-2 bg-gray-800/50 rounded-lg">
      <button
        onClick={onToggle}
        className={`p-2 rounded-lg transition-colors ${
          enabled 
            ? 'bg-orange-500/20 text-orange-400 border border-orange-500/50' 
            : 'bg-gray-700/50 text-gray-400 hover:text-orange-400'
        }`}
      >
        <Flame className="w-5 h-5" />
      </button>

      {enabled && (
        <motion.div
          initial={{ opacity: 0, width: 0 }}
          animate={{ opacity: 1, width: 'auto' }}
          className="flex items-center gap-1"
        >
          {timerOptions.map((t) => (
            <button
              key={t}
              onClick={() => onTimerChange(t)}
              className={`px-2 py-1 text-xs rounded ${
                timer === t
                  ? 'bg-orange-500 text-white'
                  : 'bg-gray-700 text-gray-400 hover:bg-gray-600'
              }`}
            >
              {t >= 60 ? `${t / 60}m` : `${t}s`}
            </button>
          ))}
        </motion.div>
      )}
    </div>
  );
}

export default SelfDestructMessage;
